def convert_tounix(timestring):
    isoconversion = ciso8601.parse_datetime(timestring)
    returntime = time.mktime(isoconversion.utctimetuple())*1e3 + isoconversion.microsecond/1e3
    return returntime

def convert_fromunix(unixtime):
    timeint = int(unixtime) 
    timeint /= 1e3
    return datetime.datetime.utcfromtimestamp(timeint)

