            continue
        if current_time == day["business_hours"]:
            picked_day = day["day_of_week"]
            for key in day_of_week_translator.keys():
                if key in picked_day:
                    picked_day = key
                    break
            current_day = day_of_week_translator[picked_day][1]
            continue
        else:
            if current_day == "":
                new_line = start_day + ": " + str(current_time)
            else:
                new_line = start_day + " - " + current_day + ": " + str(current_time)
            result.append(new_line)
            current_day = ""
            picked_day = day["day_of_week"]
            for key in day_of_week_translator.keys():
