def extract_tag_from_text(text, tag):
    patterns = re.findall(r'\['+ tag + '\](.*?)\[/' + tag + '\]', text, re.DOTALL)
    # Combine all occurrences found, separated by a space if needed
    combined_text = ' '.join(patterns).strip()
    return combined_text

def extract_full_text(text):
    combined_text = re.sub(r'\[.*?\]', '', text)
    combined_text = re.sub(r'\s+', ' ', combined_text)
    return combined_text

